mapper.update:
update orders
cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, pay_status = #{payStatus}, pay_method = #{payMethod}, checkout_time = #{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}, delivery_status = #{deliveryStatus}, estimated_delivery_time = #{estimatedDeliveryTime},
where id = #{id}

mapper.select:
select o.* from orders o


        and o.number like concat('%', #{number}, '%')


        and o.phone like concat('%', #{phone}, '%')


        and o.user_id = #{userId}


        and o.status = #{status}


        and o.order_time >= #{beginTime}


        and o.order_time <= #{endTime}


order by o.order_time desc
